import React, { useState } from 'react';
import M from 'materialize-css';
import { useHistory } from 'react-router-dom';
import '../../App.css';

const CreatePost = () => {
  const history = useHistory();
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [image, setImage] = useState('');
  const [url, setUrl] = useState('');

  const postDetails = () => {
    const data = new FormData();
    data.append('file', image);
    data.append('upload_preset', 'instagram');
    data.append('cloud_name', 'leegodden');

    fetch('https://api.cloudinary.com/v1_1/leegodden/image/upload', {
      method: 'post',
      body: data
    })
      .then((res) => res.json())
      .then((data) => {
        setUrl(data.url);
      })
      .catch((err) => {
        console.log(err);
      });

    fetch('/createpost', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        title,
        body,
        pic: url
      })
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.error) {
          M.toast({ html: data.error, classes: '#e57373 red lighten-2' });
        } else {
          M.toast({ html: 'Post created', classes: '#1e88e5 blue darken-1' });
          history.push('/');
        }
      });
  };

  return (
    <div
      className="card input-filed"
      style={{
        margin: '10px auto',
        maxWidth: '500px',
        padding: '20px',
        textAlign: 'center'
      }}
    >
      <input
        type="text"
        placeholder="title"
        value={title}
        onChange={(event) => setTitle(event.target.value)}
      />
      <input
        type="text"
        placeholder="body"
        value={body}
        onChange={(event) => setBody(event.target.value)}
      />
      <div className="file-field input-field">
        <div className="btn #64b5f6 blue lighten">
          <span>file</span>
          <input
            type="file"
            onChange={(event) => setImage(event.target.files[0])}
          />
        </div>
        <div className="file-path-wrapper">
          <input className="file-path validate" type="text" />
        </div>
      </div>
      <button
        className="btn waves-effect waves-light #64b5f6 blue lighten"
        onClick={() => postDetails()}
      >
        Upload
      </button>
    </div>
  );
};

export default CreatePost;
